INSERT INTO `type` VALUES (1, '可回收物', 'http://wx2.sinaimg.cn/large/006a7eb0ly1g4ydtdcdqpj305k05kq2u.jpg', 'https://wx2.sinaimg.cn/large/006a7eb0ly1g4ydt6lqtsj305p05kjr9.jpg', 'RECYCLABLE', '是指适宜回收和资源化利用的生活垃圾，包括脂类，塑料、金属、玻璃、木材、织物和电子废弃物', 1);
INSERT INTO `type` VALUES (2, '有害垃圾', 'http://wx1.sinaimg.cn/large/006a7eb0ly1g4ydumh80ej305k05kmx2.jpg', 'https://wx4.sinaimg.cn/large/006a7eb0ly1g4yduf9rdcj305k05kq2s.jpg', 'HAZARDOUS', '是指对人体健康或者自然环境造成直接或潜在危害的生活垃圾，包括废电池、废弃药品、废杀虫剂、废水银产品等', 1);
INSERT INTO `type` VALUES (3, '湿垃圾', 'https://wx1.sinaimg.cn/large/006a7eb0ly1g4ydvsmm6vj305k05kglk.jpg', 'https://ws2.sinaimg.cn/large/006a7eb0ly1g4ydvmnuprj305s05kaa1.jpg', 'HOUSEHOULD', '既易腐垃圾，厨余垃圾、餐厨垃圾，是指餐饮垃圾、厨余垃圾及废弃食用油脂和集贸市场有机垃圾等易腐蚀性垃圾，包括废弃的食品、蔬菜、瓜果皮核以及家庭产生的花草、落叶等', 1);
INSERT INTO `type` VALUES (4, '干垃圾 ', 'http://wx3.sinaimg.cn/large/006a7eb0ly1g4ydtv9epmj305k05kgli.jpg', 'https://ws1.sinaimg.cn/large/006a7eb0ly1g4ydqljhubj305k05k3yd.jpg', 'RESIDOUAL', '既其他垃圾，是指可回收物、有害垃圾、湿垃圾以外的其他生活废弃物。包括砖瓦陶瓷、普通一次性电池（碱性电池）、受污染的一次性餐盒、卫生间废纸等', 1);
